    },
    {
      text: 'Learn about Quality Assurance',
      isCompleted: false
    },
    {
      text: 'Learn about Food',
      isCompleted: false
    }
  ]);

  return (
    <div className="app">
      <div className="todo-list">
        {todos.map((todo, index) => {
          <Todo  />
        })}
      </div>
    </div>
  );
}

export default App;
